import React, { Component } from "react";
import LocalList from "./LocalList";
import WeatherForm from "./WeatherForm"

const API_KEY = "7e65a726f9556ca15ad913517079de8e";

class WeatherContainer extends Component {
    state = {
        temperature: undefined,
        city: undefined,
        country: undefined,
        humidity: undefined,
        description: undefined,
        error: undefined
    }



    getWeather = async (e) => {
        e.preventDefault();
        const city = e.target.elements.city.value;
        const country = e.target.elements.country.value;

        //const api_call = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=${API_KEY}&units=imperial`);
        const api_call = await fetch(`https://api.openweathermap.org/data/2.5/forecast/daily?q=${city},${country}&cnt=7&appid=${API_KEY}&units=imperial`);
        const data = await api_call.json();

        if (city && country) {
            console.log(data);
            //     this.setState({
            //         temperature: data.main.temp,
            //         city: data.name,
            //         country: data.sys.country,
            //         humidity: data.main.humidity,
            //         description: data.weather[0].description,
            //         windSpeed: data.wind.speed,
            //         icon: data.weather.icon,
            //         error: ""
            //     })
            // } else {
            //     this.setState({
            //         temperature: undefined,
            //         city: undefined,
            //         country: undefined,
            //         humidity: undefined,
            //         description: undefined,
            //         error: "please choose a city and country"
            //     })
        }
    }

    render() {
        return (
            <div>
                <WeatherForm getWeather={this.getWeather} />
                <LocalList
                    temperature={this.state.temperature}
                    city={this.state.city}
                    country={this.state.country}
                    humidity={this.state.humidity}
                    description={this.state.description}
                    error={this.state.error}
                    windSpeed={this.state.windSpeed}
                    icon={this.state.icon}
                />
            </div>
        );
    }
}

export default WeatherContainer;
