import React from 'react'

const DailyForcast = ({ DailyForcast }) => {
    let formatedTime = '';
    let dailyForcast = DailyForcast.map((day, i) => {
        //converts UNIX time into military time
        let a = new Date(day.time * 1000);
        let months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
        let month = months[a.getMonth()];
        let date = a.getDate();
        var formattedTime = month + ' ' + date;
        console.log(day)
        return (
            <div key={i}>
                <h4>High at {formatedTime}</h4>
                <h1>{day.apparentTemperatureHigh}/{day.apparentTemperatureLow}</h1>
            </div>
        )
    })
    return (
        <div>
            <div>{dailyForcast}</div>
        </div>
    )

}
export default DailyForcast
