import React, { Component } from "react";
import LocalList from "./LocalList";
import WeatherForm from "./WeatherForm";
import ForcastList from "./ForcastList";

//const API_KEY = "7e65a726f9556ca15ad913517079de8e";
const API_KEY2 = "ce43c0fc2b233e6004791355f0686c19";

class WeatherContainer extends Component {
    state = {
        forcast: undefined,
        temperature: undefined,
        tempMax: undefined,
        tempMin: undefined,
        city: undefined,
        country: undefined,
        coord: undefined,
        humidity: undefined,
        description: undefined,
        windSpeed: undefined,
        error: undefined
    }



    getWeather = async (e) => {
        e.preventDefault();
        const lat = e.target.elements.lat.value;
        const lng = e.target.elements.lng.value;


        //lat: 39.742,
        //lng: -104.9915

        //const api_call = await fetch(`https://api.openweathermap.org/data/2.5/forecast/?q=${city},${country}&cnt=5&appid=${API_KEY}&units=imperial`);
        const api_call = await fetch(`https://cors-anywhere.herokuapp.com/https://api.darksky.net/forecast/${API_KEY2}/${lat},${lng}`);
        const data = await api_call.json();

        //converts UNIX time into military time

        let a = new Date(data.currently.time * 1000);
        let months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
        let year = a.getFullYear();
        let month = months[a.getMonth()];
        let date = a.getDate();
        let hour = a.getHours();
        let min = a.getMinutes();
        let sec = a.getSeconds();
        let formattedTime = month + ' ' + date + ' ' + year + ' ' + hour + ':' + min + ':' + sec;

        if (lat && lng) {
            console.log(data);
            console.log(formattedTime);
            //console.log(data.list[0].main.temp_min);
            //     this.setState({
            //         forcast: data.list,
            //         temperature: data.list[0].main.temp,
            //         tempMax: data.list[0].main.temp_max,
            //         tempMin: data.list[0].main.temp_min,
            //         city: data.city.name,
            //         country: data.city.country,
            //         coord: data.city.coord,
            //         humidity: data.list[0].main.humidity,
            //         description: data.list[0].weather[0].description,
            //         windSpeed: data.list[0].wind.speed,

            //         //icon: data.weather.icon,
            //         error: ""
            //     })
            // } else {
            //     this.setState({
            //         temperature: undefined,
            //         tempMax: undefined,
            //         tempMin: undefined,
            //         city: undefined,
            //         country: undefined,
            //         coord: undefined,
            //         humidity: undefined,
            //         description: undefined,
            //         windSpeed: undefined,
            //         error: "please choose a city and country"
            //     })
        }
    }

    render() {
        return (
            <div>
                <WeatherForm getWeather={this.getWeather} />
                <LocalList
                    temperature={this.state.temperature}
                    city={this.state.city}
                    country={this.state.country}
                    humidity={this.state.humidity}
                    description={this.state.description}
                    error={this.state.error}
                    windSpeed={this.state.windSpeed}
                    icon={this.state.icon}
                    forcast={this.state.forcast}
                />
            </div>
        );
    }
}

export default WeatherContainer;
